import React, {useState, useEffect, useContext } from 'react'
import {useHttp} from '../hooks/http.hook'
import { AuthContext } from '../context/AuthContext'
import {useHistory} from 'react-router-dom'

export const EditPayer = ({ payer }) => {
    const history = useHistory()
    const auth = useContext(AuthContext)
    const {request} = useHttp()
    const [payers, setPayers] = useState(payer.payerCode)
    const [name, setName] = useState(payer.payerName)
    const [payerType, setPayerType] = useState(payer.payer)
    const [tax, setTax] = useState(payer.payer)
    const [income, setIncome] = useState(payer.payer)
    const [period, setPeriod] = useState('')
    const [phone, setPhone] = useState('')
    const [email, setEmail] = useState('')

    useEffect(() => {
        window.M.updateTextFields()
    }, [])

    const pressHandler = async event => {
            try {
                const data = await request('/api/payer/createpayer', 'POST', 
                {
                    payerCode: payers, 
                    payerName: name, 
                    payerType: payerType, 
                    payerTax: tax,
                    payerIncome: income,
                    payerPeriod: period,
                    payerPhone: phone,
                    payerEmail: email
                }, 
                {
                    Authorization: `Bearer ${auth.token}`
                })
                history.push(`/detail/${data.payers._id}`)
            } catch(e) {}

    }

    return (
        <div className="row">
            <div className="col s8 offset-s2">
                <h1 className="create-peyer-title">Створити платника</h1>
            </div>

            {/* <input type="hidden" name="_id" value={data.payer._id} />
            <input type="hidden" name="_id" value="{{employee._id}}"></input> */}

            <div className="col s8 offset-s2" style={{ paddingTop: '2rem' }}>

                <div className="input-con">
                    <input
                        className="create-input"
                        id="payer-code"
                        type="text"
                        value={payers}
                        onChange={e => setPayers(e.target.value)}
                    />
                    <label className="create-label" htmlFor="payer-code">Код платника</label>
                </div>

                <div className="input-con">
                    <input
                        className="create-input"
                        id="payer-name"
                        type="text"
                        value={payer.payerName}
                        onChange={e => setName(e.target.value)}
                    />
                    <label className="create-label" htmlFor="payer-name">ПІП Платника</label>
                </div>

                <div className="input-con">
                    <input
                        className="create-input"
                        id="payer-type"
                        type="text"
                        value={payer.payerType}
                        onChange={e => setPayerType(e.target.value)}
                    />
                    <label className="create-label" htmlFor="payer-type">Вид організації</label>
                </div>

                <div className="input-con">
                    <input
                        className="create-input"
                        id="payer-tax"
                        type="text"
                        value={payer.payerTax}
                        onChange={e => setTax(e.target.value)}
                    />
                    <label className="create-label" htmlFor="payer-tax">Вид податку</label>
                </div>

                <div className="input-con">
                    <input
                        className="create-input"
                        id="payer-income"
                        type="text"
                        value={payer.payerIncome}
                        onChange={e => setIncome(e.target.value)}
                    />
                    <label className="create-label" htmlFor="payer-income">Річний дохід</label>
                </div>

                <div className="input-con">
                    <input
                        className="create-input"
                        id="payer-period"
                        type="date"
                        value={payer.payerPeriod}
                        onChange={e => setPeriod(e.target.value)}
                    />
                    <label className="create-label" htmlFor="payer-period">Кінець податкового періоду</label>
                </div>

                <div className="input-con">
                    <input
                        className="create-input"
                        id="payer-phone"
                        type="text"
                        value={payer.payerPhone}
                        onChange={e => setPhone(e.target.value)}
                    />
                    <label className="create-label" htmlFor="payer-phone">Телефон</label>
                </div>

                <div className="input-con">
                    <input
                        className="create-input"
                        id="payer-email"
                        type="text"
                        value={payer.payerEmail}
                        onChange={e => setEmail(e.target.value)}
                    />
                    <label className="create-label" htmlFor="payer-email">Email</label>
                </div>

                <div className="btn-con">
                    <button
                        className="create-btn red accent-2"
                        onClick={pressHandler}
                    >Створити</button>
                </div>
            </div>
        </div>
    )
}
