import React from 'react'

export const PayerCard = ({ payer }) => {

    let chose2 = 1
    switch (payer.payerType) {
        case '1':
            chose2 = 0.5
            const definition = 'ФОП'
            break;
        case '2':
            chose2 = 1.2
            break;
        case '3':
            chose2 = 2
            break;
    }

    let chose1 = 1
    switch (payer.payerTax) {
        case '1':
            chose1 = 5
            break;
        case '2':
            chose1 = 22
            break;
        case '3':
            chose1 = 20
            break;
    }
    const calc = payer.payerIncome * chose1 * chose2 / 100
  return (
    <>
      <h2>Платник</h2>

      <p>Код платника: {payer.payerCode}</p>
      <p>ПІП: {payer.payerName}</p>
      <p>Вид організації: {definitionType}</p>
      <p>Вид податку: {payer.payerTax}</p>
      <p>Річний дохід: {payer.payerIncome}</p>
      <p>Кінець аодаткового періоду: {payer.payerPeriod}</p>
      <p>Телефон: {payer.payerPhone}</p>
      <p>Email: {payer.payerEmail}</p>
      <strong>Податок: {calc}</strong>
      <p>Дата создания: <strong>{new Date(payer.date).toLocaleDateString()}</strong></p>
    </>
  )
}
