import React, {useState, useEffect, useContext} from 'react'
import {useHttp} from '../hooks/http.hook'
import { AuthContext } from '../context/AuthContext'
import {useHistory} from 'react-router-dom'

export const CreatePage = () => {
    const history = useHistory()
    const auth = useContext(AuthContext)
    const {request} = useHttp()
    const [payer, setPayer] = useState('')

    useEffect(() => {
        window.M.updateTextFields()
    }, [])

    const pressHandler = async event => {
        if(event.key === 'Enter') {
            try {
                const data = await request('/api/payer/createpayer', 'POST', {payerCode: payer}, {
                    Authorization: `Bearer ${auth.token}`
                })
                history.push(`/detail/${data.payer._id}`)
            } catch(e) {}
        }
    }

    return (
        <div className="row">
            <div className="col s8 offset-s2" style={{ paddingTop: '2rem' }}>
                <input
                    className="in"
                    id="payer-code"
                    type="text"
                    value={payer}
                    onChange={e => setPayer(e.target.value)}
                    onKeyPress={pressHandler}
                />
                <label htmlFor="payer-code">Код платника</label>

                <input
                    id="payer-name"
                    type="text"
                    value={payer}
                    onChange={e => setPayer(e.target.value)}
                    onKeyPress={pressHandler}
                />
                <label htmlFor="payer-name">ПІП Платника</label>

                <input
                    id="payer-type"
                    type="text"
                    value={payer}
                    onChange={e => setPayer(e.target.value)}
                    onKeyPress={pressHandler}
                />
                <label htmlFor="payer-type">Вид організації</label>

                <input
                    id="payer-tax"
                    type="text"
                    value={payer}
                    onChange={e => setPayer(e.target.value)}
                    onKeyPress={pressHandler}
                />
                <label htmlFor="payer-tax">Вид податку</label>

                <input
                    id="payer-income"
                    type="text"
                    value={payer}
                    onChange={e => setPayer(e.target.value)}
                    onKeyPress={pressHandler}
                />
                <label htmlFor="payer-income">Річний дохід</label>

                <input
                    id="payer-period"
                    type="text"
                    value={payer}
                    onChange={e => setPayer(e.target.value)}
                    onKeyPress={pressHandler}
                />
                <label htmlFor="payer-period">Кінець податкового періоду</label>

                <input
                    id="payer-phone"
                    type="text"
                    value={payer}
                    onChange={e => setPayer(e.target.value)}
                    onKeyPress={pressHandler}
                />
                <label htmlFor="payer-phone">Телефон</label>

                <input
                    id="payer-email"
                    type="text"
                    value={payer}
                    onChange={e => setPayer(e.target.value)}
                    onKeyPress={pressHandler}
                />
                <label htmlFor="payer-email">Email</label>
            </div>
        </div>
    )
}
